import t from"eventemitter3";class e extends t{constructor(t){super(),this.list=new Map,this.core=t}getSession(t){const e=this.list.get(t);if(!e)throw new Error(`can not get ${t}`);return e}createSession(t){const e=t.sessionId;if(this.list.get(e))throw new Error(`can not create ${e}`);return this.list.set(e,t),t}updateSession(t,e){const s=this.list.get(t);if(!s)throw new Error(`can update ${t}`);const i=Object.assign(s,e);return this.list.set(t,i),this.emit("update",i),i}deleteSession(t){const e=this.list.get(t);return this.list.delete(t),e}}e.moduleName="V2NIMSessionService";export{e as default};
